
public class posneg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i= Integer.parseInt(args[0]);
        if(i>0)
        {
          System.out.println(i+" "+"is positive");
          
        }
        else
        {
        	System.out.println(i+" "+"is negative");
        }
	}

}
